import React, { useState } from "react";
import { Form, FormGroup, InputGroup, Button } from "react-bootstrap";
import { useConversations } from "../contexts/ConversationsProvider";

export default function OpenConversation() {
  const [text, setText] = useState("");
  const { sendMessage, selectedConversation } = useConversations();

  function handleSubmit(e) {
    e.preventDefault();

    sendMessage(
      selectedConversation.recipients.map((r) => r.id),
      text
    );
    setText("");
  }

  return (
    <div className="d-flex flex-column flex-grow-1">
      <div className="flex-grow-1 overflow-auto"></div>
      <div className="h-100 d-flex flex-column align-items-start justify-content-end px-3">
        {selectedConversation.messages.map((message, index) => {
          return (
            <div key={index} className="my-1 d-flex flex-column">
              <div
                className={`rounded px-2 py-1 ${
                  message.fromMe ? "bg-primary text-white" : "border"
                }`}
              >
                {message.text}
              </div>
              <div className={`text-muted small`}>{message.fromMe ? "You" : message.senderName}</div>
            </div>
          );
        })}
      </div>
      <Form onSubmit={handleSubmit}>
        <FormGroup className="m-2">
          <InputGroup>
            <Form.Control
              as="textarea"
              required
              value={text}
              onChange={(e) => setText(e.target.value)}
              style={{ height: "75px", resize: "none" }}
            />
            <InputGroup.Append className="d-flex">
              <Button type="sumbit">Send</Button>
            </InputGroup.Append>
          </InputGroup>
        </FormGroup>
      </Form>
    </div>
  );
}
